def pol1():
    text = ''
    pol = random.randint(0, 1)
    if pol == 0:
        text = 'Мужчина '
    elif pol == 1:
        text = 'Женщина '
    sex = random.randint(1, 100)
    if sex < 10:
        text = text + '(би)'
    if sex > 9 and sex < 16:
        text = text + '(гомо)'
    elif sex > 15:
        text = text + '(гетеро)'
    return text

