    if vozrast >= 15 and vozrast < 35:
        text = str(random.randint(21, 27))
    if vozrast >= 35 and vozrast < 57:
        text = str(random.randint(28, 34))
    if vozrast >= 57 and vozrast < 75:
        text = str(random.randint(35, 39))
    if vozrast >= 75 and vozrast < 84:
        text = str(random.randint(40, 46))
    if vozrast >= 84 and vozrast < 91:
        text = str(random.randint(47, 53))
    if vozrast >= 91 and vozrast < 96:
        text = str(random.randint(54, 60))
    if vozrast >= 96 and vozrast < 99:
        text = str(random.randint(61, 67))
